import turtle
t=turtle.Pen()
t.showturtle()

t.penup()
t.goto(-140,120)
t.pendown()
t.color('blue')
t.resizemode('auto')
t.pensize(4)
t.circle(50)

t.penup()
t.goto(-25,120)
t.pendown()
t.color('black')
t.resizemode('auto')
t.pensize(4)
t.circle(50)

t.penup()
t.goto(90,120)
t.pendown()
t.color('red')
t.resizemode('auto')
t.pensize(4)
t.circle(50)

t.penup()
t.goto(-80,70)
t.pendown()
t.color('yellow')
t.resizemode('auto')
t.pensize(4)
t.circle(50)

t.penup()
t.goto(30,70)
t.pendown()
t.color('green')
t.resizemode('auto')
t.pensize(4)
t.circle(50)

#vs 防止画图框闪退
turtle.done()
